

def merge_the_tools(string, k):
    import functools

    def my_func(acc, char):
        if char in acc:
            return acc
        else:
            return acc + char
    index = 0
    string_len = len(string)
    k_factor = int(string_len/k)
    print(f'String length is {string_len}')
    print(f'K_factor is {k_factor}')
    while(index < string_len):
        stop = index + k
        sub = string[index: stop]
        sub = functools.reduce(my_func, sub, '')
        print(sub)
        index += k


string = ""

merge_the_tools(string, 3452)
